const companyString = Inputs.Inspectors;
const companies = companyString.split(', ');
Outputs.comp = companies

const supabase = Noodl.Variables.supabase;

if (!supabase) throw new Error('Supabase client not found');

const emails = Inputs.Email;
const userDataList = [];

async function fetchData(email) {
  try {
    const { data, error } = await supabase
      .from('user_master')
      .select('first_name, certificate_num') // Specify the columns you want to fetch
      .eq('login_email', email);

    if (error) {
      console.error(error);
      Outputs.Failure();
    } else {
      const userData = {
        first_name: data[0]?.first_name || 'N/A',
        certificate_num: data[0]?.certificate_num || 'N/A'
      };

      userDataList.push(userData);
      Outputs.Success();
    }
  } catch (error) {
    console.error(error);
    Outputs.Failure();
  }
}

async function fetchDataForEmails() {
  for (const email of emails) {
    await fetchData(email);
  }

  Outputs.Inspectors = userDataList; 
  console.log(userDataList)// Output the combined list
  Outputs.close();
}

fetchDataForEmails();

