const supabase = Noodl.Variables.supabase;

if (!supabase) throw new Error('Supabase client not found');

const exampleData = {
  inspection_date: Inputs.inspection_date,
  inspection_start_time: Inputs.inspection_start_time,
  start_time: Inputs.start_time,
  inspection_end_time: Inputs.inspection_end_time,
  project_location: Inputs.project_location,
  state_of_survey_area: Inputs.state_of_survey_area,
  condition_of_survey_area: Inputs.condition_of_survey_area,
  lab_id: Inputs.lab_id,
  follow_up_inspection: Inputs.follow_up_inspection,
  project_manager_id: Inputs.project_manager_id,
  investigator_id: Inputs.investigator_id,
  inspection_request_id: Inputs.inspection_request_id,
};

try {
  // Insert data into the field_report table
  const { data, error } = await supabase
    .from('field_report')
    .insert([exampleData]);

  if (error) {
    console.error(error);
  } else {
    console.log('Data inserted into field_report table:', data);

    try {
      // get id of the last created field report
      const { data, error } = await supabase
        .from('field_report')
        .select('id')
        .eq('inspection_request_id', Inputs.inspection_request_id)
        .order('id', { ascending: false })
        .limit(1);

      if (error) {
        console.error(error);
      } else {
        console.log('Data of field_report', data);
        Outputs.Success();
        console.log(data[0].id);
        Outputs.FieldId = data[0].id;
      }
    } catch (error) {
      console.error(error);
    }
  }
} catch (error) {
  console.error(error);
}

