if (!Inputs.Triggered) return;
const supabase = Noodl.Variables.supabase;

if (!supabase) throw new Error('Supabase client not found');
const ins_id = Inputs.Ins;

try {
  const { data: data_ins, error: error_ins } = await supabase
    .from('inspection_request')
    .select()
    .eq('id', ins_id);

  if (error_ins) {
    console.error("Error fetching inspection_request:", error_ins);
    Outputs.Failure();
    return;
  }

  const { data: data_fr, error: error_fr } = await supabase
    .from('field_report')
    .select()
    .eq('inspection_request_id', ins_id);

  if (error_fr) {
    console.error("Error fetching field_report:", error_fr);
    Outputs.Failure();
    return;
  }

  const pro_mana = data_fr[0].project_manager_id
  const { data: data_pm, error: error_pm } = await supabase
    .from('user_master')
    .select()
    .eq('id', pro_mana);

  if (error_pm) {
    console.error("Error fetching field_report:", error_pm);
    Outputs.Failure();
    return;
  }

  const { data: data_samp, error: error_samp } = await supabase
    .from('samples_collected')
    .select()
    .eq('inspection_request_id', ins_id)
    .order('homogenious_area_num', { ascending: true })
    .order('sample_id', { ascending: true });

  if (error_samp) {
    console.error("Error fetching samples:", error_samp);
    Outputs.Failure();
    return;
  }

  const { data: data_coc, error: error_coc } = await supabase
    .from('chain_of_custody')
    .select()
    .eq('inspection_id', ins_id);

  if (error_coc) {
    console.error("Error fetching chain_of_custody:", error_coc);
    Outputs.Failure();
    return;
  }

  const { data: data_lab, error: error_lab } = await supabase
    .from('lab_master')
    .select()
    .eq('id', data_fr[0].lab_id);

  if (error_lab) {
    console.error("Error fetching chain_of_custody:", error_lab);
    Outputs.Failure();
    return;
  }
  const all_ins = data_ins[0].all_inspectors;
  const inspectors_info = all_ins.split(', ');
  // console.log(inspectors_info);

  const userDataList = [];
  async function fetchData(email) {
    try {
      const { data, error } = await supabase
        .from('user_master')
        .select() // Specify the columns you want to fetch
        .eq('login_email', email);

      if (error) {
        console.error(error);
        Outputs.Failure();
      } else {
        const userData = {
          first_name: data[0]?.first_name,
          last_name: data[0]?.last_name,
          certificate_num: data[0]?.certificate_num,
          license_image: data[0]?.license_image
        };

        userDataList.push(userData);
        // Outputs.Success();
      }
    } catch (error) {
      console.error(error);
      Outputs.Failure();
    }

  }

  async function fetchDataForEmails() {
    for (const email of inspectors_info) {
      await fetchData(email);
    }


    // console.log(userDataList)

  }
  fetchDataForEmails()
    .then(() => {
      Outputs.Success();
    });


  let sample_collected = [];
  let uniqueHomogeniousAreas = {};

  data_samp.forEach(sample => {
    const { material_name, homogenious_area_num, test_type_pacm } = sample;
    if (!test_type_pacm && !uniqueHomogeniousAreas[homogenious_area_num]) {
      uniqueHomogeniousAreas[homogenious_area_num] = true;
      sample_collected.push({ nm: material_name });
    }
  });

  data_samp.forEach(entry => {
    if (entry.acm) {
      entry.classification = "ACM";
    } else if (entry.non_acm) {
      entry.classification = "Non-ACM";
    }
    else {
      entry.classification = "-"
    }
  });

  let sample_acm = {};
  let sample_pacm = {};

  data_samp.forEach(sample => {
    if (sample.acm) {
      const homogenious_area_num = sample.homogenious_area_num;
      if (!sample_acm[homogenious_area_num]) {
        sample_acm[homogenious_area_num] = sample;
      }
    }
    if (sample.test_type_pacm) {
      const homogenious_area_num = sample.homogenious_area_num;
      if (!sample_pacm[homogenious_area_num]) {
        sample_pacm[homogenious_area_num] = sample;
      }
    }
  });

  const sample_acm_array = Object.values(sample_acm);
  const sample_pacm_array = Object.values(sample_pacm);

  let sample_result = [];
  let seenHomogeniousAreaNums = new Set();

  data_samp.forEach(sample => {
    const {
      id,
      homogenious_area_num,
      location_in_building,
      inspection_type,
      sample_photo,
      material_name,
      material_qty,
      material_unit,
      material_note,
      material_condition,
      material_classification,
      test_type_plm,
      test_type_plm_nob,
      test_type_tem_nob,
      sample_id,
      test_type_pacm,
      acm,
      non_acm,
      classification
    } = sample;

    if (!test_type_pacm && !seenHomogeniousAreaNums.has(homogenious_area_num)) {
      seenHomogeniousAreaNums.add(homogenious_area_num);

      const allSampleIds = data_samp
        .filter(s => s.homogenious_area_num === homogenious_area_num)
        .map(s => s.sample_id)
        .join(', ');

      const newSample = {
        id,
        inspection_request_id: ins_id,
        field_report_id: data_fr[0].id,
        homogenious_area_num,
        location_in_building,
        inspection_type,
        sample_photo,
        material_name,
        material_qty,
        material_unit,
        material_note,
        material_condition,
        material_classification,
        test_type_plm,
        test_type_plm_nob,
        test_type_tem_nob,
        sample_id,
        test_type_pacm,
        acm,
        non_acm,
        classification,
        new_samp_id: allSampleIds
      };
      sample_result.push(newSample);
    }
  });

  const output = {
    inspectors: userDataList,
    project_manager_name: data_pm[0].first_name + " " + data_pm[0].last_name,
    lab_inf: data_lab[0].lab_name + ", ELAP : " + data_lab[0].elap_number + ", Address : " + data_lab[0].address_1 + ", " + data_lab[0].state + ", " + data_lab[0].city + ", " + data_lab[0].zip_code,
    inspection_request_id: data_ins[0].id,
    field_report_id: data_fr[0].id,
    chain_of_custody_id: data_coc[0].id,
    ...data_ins[0],
    ...data_fr[0],
    ...data_coc[0],
    samples: data_samp,
    sample_collected: sample_collected,
    sample_acm: sample_acm_array,
    sample_pacm: sample_pacm_array,
    sample_result: sample_result
  };

  // console.log(userDataList)
  console.log('111111111' + output)
  Outputs.Samp = data_samp
  Outputs.PdfData = output;
  Outputs.Inspectors = userDataList
  // Outputs.Success();
  // Outputs.Json = (JSON.stringify(output, null, 2));
  // Outputs.sample_collected = sample_collected;

} catch (error) {
  console.error("Unexpected error:", error);
  Outputs.Failure();
}
