const userManagerDataList =  [{Label:"New HA",
                              Value:"0"}];
const uniqueMaterialNames = new Set();

async function fetchUserDataByCompanyId(ins_Id) {
  try {
    const { data, error } = await supabase
      .from('samples_collected')
      .select('material_name, homogenious_area_num')
      .eq('inspection_request_id', ins_Id)
      .order('homogenious_area_num', { ascending: false });

    if (error) {
      console.error(error);
      Outputs.Failure();
    } else {
      data.forEach(item => {
        if (!uniqueMaterialNames.has(item.material_name)) {
          const userData = {
            Label: item.material_name || 'N/A',
            Value: String(item.homogenious_area_num) || 'N/A'
          };
          userManagerDataList.push(userData);
          uniqueMaterialNames.add(item.material_name);
        }
      });

      Outputs.Investigator = userManagerDataList;
      Outputs.Success();
      
      // if(ha_value == "{HA}"){
      //   Outputs.Value = "";
      // }
      // else{
      //   Outputs.Value = ha_value;
      // }
    }
  } catch (error) {
    console.error(error);
    Outputs.Failure();
  }
}

async function fetchDataForCompanyId() {
  await fetchUserDataByCompanyId(ins_Id);
