    .select()
    .eq('company', company);

  if (error) {
    console.error(error)
    Outputs.Failure();
  } else {
    const transformedData = data.map(item => ({
      Label: item.name,
      Value: item.name
    }));

    Outputs.Material = transformedData;
    Outputs.Success();
  }
} catch (error) {
  console.error(error)
  Outputs.Failure();
}

