function formatTime(_hours, _minutes) {
    const hours = _hours !== undefined ? _hours : 0;
    const minutes = _minutes !== undefined ? _minutes : 0;

    let period = 'AM';
    let formattedHours = hours;

    if (hours >= 12) {
        period = 'PM';
        formattedHours = (hours % 12) || 12;
    }

    return (
        ((formattedHours < 10) ? ('0' + formattedHours) : formattedHours) +
        ':' +
        ((minutes < 10) ? ('0' + minutes) : minutes) +
        ' ' +
        period
    );
}




function parseTime(_timeString) {
    if(_timeString === undefined || _timeString.length === 0) 
        return

    let timeString = _timeString.trim()
    let hours,minutes

    if(timeString.indexOf(':') !== -1) {
        var parts = timeString.split(':')
        hours = parseInt(parts[0])
        minutes = parseInt(parts[1])
    }
    else {
        if(timeString.length === 4) { // assume formate like 1130
            hours = parseInt(timeString[0] + timeString[1])
            minutes = parseInt(timeString[2] + timeString[3])
        }
        else if(timeString.length === 3) { // Assume format like 630
            hours = parseInt(timeString[0])
            minutes = parseInt(timeString[1] + timeString[2])
        }
        else {
            let nbr = parseInt(timeString)
            if(nbr >= 24) {
                minutes = nbr
                hours = 0
            }
            else {
                hours = nbr
                minutes = 0
            }
        }
    }

    return {hours,minutes}
}

var timeInput

Script.Signals.TextInputFocused = () => {
    Component.Object.TextInputIsFocused = true

    // If the text input is focused and we are not running on a mobile device
    // launch the popup
    if(!isMobile()) {
        Script.Outputs.PopupMounted = true;

    }
}

Script.Signals.TextInputBlurred = () => {
    Component.Object.TextInputIsFocused = false
    const time = parseTime(Script.Inputs.TimeString)
    if(time) {
        Component.Object.Hours = time.hours
        Component.Object.Minutes = time.minutes
        Script.Outputs.TimeSelected()
    }

    Script.Outputs.PopupMounted = false
}

Script.Signals.DidMount = () => {
    if(Component.Object.Hours !== undefined || Component.Object.Minutes !== undefined) {
        let formattedTime = formatTime(Component.Object.Hours,Component.Object.Minutes)
        Component.Object['Formatted Time'] = formattedTime
    }

    Component.Object.on('change',(ev) => {
        if(ev.name === 'Hours' || ev.name === 'Minutes') {
            const hours = Component.Object.Hours
            const minutes = Component.Object.Minutes

            let formattedTime = formatTime(hours,minutes)
            Component.Object['Formatted Time'] = formattedTime

            if(timeInput !== undefined) {
                timeInput.value = formattedTime
            }


        }
    })

    if(isMobile()) {
        // On Mobile create a native date input hidden
        // under the icon, clicking it will show the
        // native picker
        const elem = Script.Inputs.TextInput.getDOMElement()

        timeInput = document.createElement('input')
        timeInput.style.cssText = `
            display: block;
            position: absolute;
            width: 100%;
            height: 100%;
            opacity: 0;
            cursor: pointer;
            min-width: 100%;
            margin: 0;
            padding: 0;
            border: 0;
            border-radius: 0;
        `;

        elem.parentNode.appendChild(timeInput)

        timeInput.type = "time"

        timeInput.onchange = () => {
            const time = parseTime(timeInput.value)
            Component.Object.Hours = time.hours
            Component.Object.Minutes = time.minutes
            Script.Outputs.TimeSelected()
        }
    }
}

Script.Setters.TimeString = () => {
    if(Component.Object.TextInputIsFocused) {
        const time = parseTime(Script.Inputs.TimeString)

        if(timeInput !== undefined) {
            timeInput.value = formatTime(time.hours,time.minutes)
        }
    }
}

Script.Signals.QuickPickClicked = () => {
    const quickPick = Noodl.Objects[Script.Inputs.ClickedItemId]
    Script.Outputs.BlurTextInput()
    // Allow the text input to blur
    setTimeout(() => {
        const time = parseTime(quickPick.Label)
        Component.Object.Hours = time.hours
        Component.Object.Minutes = time.minutes
        Component.Object['Formatted Time'] = quickPick.Label
        Script.Outputs.TimeSelected()
    })
}

