Script.Signals.RecoverAppState = function () {
    
    Script.Outputs.HA = localStorage.getItem('ha')
    Script.Outputs.Location = localStorage.getItem('location')
    Script.Outputs.Type = localStorage.getItem('inspe_type')
}

Script.Signals.RecoverAppState = function () {
    if (localStorage.getItem('AppState') !== null) {
        // AppState is set, you can access its value
        Noodl.Variables.AppState = localStorage.getItem('AppState');
        Script.Outputs.value = (Noodl.Variables.AppState)
    } else {
        // AppState is not set in localStorage
        Script.Outputs.value = 'AppStateIsNotSet';
    }
}

Script.Signals.SetAppState = function () {
    const language = Script.Inputs['AppState value to set']
    localStorage.setItem('AppState', language)
    Noodl.Variables.AppState = language
}

